
public class RogicalOperator1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* 논리 연산자는 둘 이상의 논리식을 병렬 형태로 제시한다.
		 * 논리식 1 && 논리식2 > A and B
		 * 논리식 1 || 논리식2 > A or B
		*/
		System.out.println((100>50) & (30>50));
		System.out.println((100>50) | (30>50));
		
	}

}
